import ToolTipComponents.PanelToolTip;
import mx.events.ToolTipEvent;

private function createCustomTip(title:String, body:String, event:ToolTipEvent):void {
   var ptt:PanelToolTip = new PanelToolTip();
   ptt.title = title;
   ptt.bodyText = body;
   event.toolTip = ptt;
}
